package rsa

import (
	"testing"
)

func TestSign(t *testing.T) {
	rsaPrivateKey := ""
	signStr := "123一二三{\"key\":\"value\"}"
	prkRsa, err := PrivateKeyRsa(rsaPrivateKey)
	if err != nil {
		t.Fatal(err)
	}
	got, err := Sign(signStr, prkRsa)
	if err != nil {
		t.Fatalf("Sign() error = %v", err)
	}
	t.Log(got)
}

func TestVerify(t *testing.T) {
	rsaPrivateKey := ""
	signStr := "123一二三{\"key\":\"value\"}"
	prkPem, err := PrivateKeyRsa(rsaPrivateKey)
	if err != nil {
		t.Fatal(err)
	}
	signature, err := Sign(signStr, prkPem)
	if err != nil {
		t.Fatalf("Sign() error = %v", err)
	}
	t.Log(signature)

	publicKeyStr := ""
	pukRsa, err := PublicKeyRsa(publicKeyStr)
	if err != nil {
		t.Fatal(err)
	}
	got, err := Verify(signStr, pukRsa, signature)
	if err != nil {
		t.Fatal(err)
	}
	if !got {
		t.Fatal("Verify() error = false")
	}
}
